import React, { useRef, useState, useContext, useEffect } from "react";

export const BookSearch = () => {
  const [inputSwitch, setInputSwitch] = useState("off"); //on is articles
  const onBookSwitchToggle = (e) => {
    e.target.value === "off" ? setInputSwitch("on") : setInputSwitch("off");

    console.log(e.target.value);
  };
  return (
    <div class="container">
      <div class="search-container">
        <form>
          <div class="switch ">
            <label>
              Books
              <input
                type="checkbox"
                value={inputSwitch}
                onChange={(e) => {
                  onBookSwitchToggle(e);
                }}
              />
              <span class="lever"></span>
              Articles
            </label>
          </div>
          <div class="input-field search-box">
            <input id="search" type="search" />
            <button class="btn small blue">Search</button>
          </div>
          <div class="book-radio">
            <p>
              <label>
                <input name="group1" type="radio" checked />
                <span id="radio-text1">Search by Title</span>
              </label>
            </p>
            <p>
              <label>
                <input name="group1" type="radio" />
                <span id="radio-text2">Search by Author</span>
              </label>
            </p>
          </div>
        </form>
      </div>
    </div>
  );
};

export default BookSearch;
