import React, { useReducer, useEffect } from "react";
import axios from "axios";
import VideosContext from "./VideosContext";
import VideosReducer from "./VideosReducer";

import { VIDEOS_ERROR, GET_VIDEOS } from "../types";

const VideosState = (props) => {
  const initialState = {
    videos: null,
    error: null,
    loading: true,
  };
  const [state, dispatch] = useReducer(VideosReducer, initialState);

  // Get Books
  const getVideos = async (query) => {
    const url = `https://youtube.googleapis.com/youtube/v3/search?q=${query}&key=${process.env.REACT_APP_YOUTUBE_API_KEY}`;
    try {
      const res = await axios.get(url);

      dispatch({
        type: GET_VIDEOS,
        payload: res.data,
      });

      // console.log("result", result);
    } catch (err) {
      dispatch({ type: VIDEOS_ERROR });
    }
  };

  return (
    <VideosContext.Provider
      value={{
        videos: state.videos,
        getVideos,
        loading: state.loading,
        error: state.error,
      }}
    >
      {props.children}
    </VideosContext.Provider>
  );
};

export default VideosState;
